"select\n    distinct d.[dashboard_external_id],\n    ds.[object_type],\n    ds.[user_group_id],\n    ds.[fake_date_num],\n    ds.[shared_counter]\nfrom \n    [Dashboards_shared] ds\nleft join \n    [Dashboards] d \non d.[dashboard_id] = ds.[dashboard_id]\nunion\nselect\n    distinct d.[dashboard_external_id],\n    'user' as object_type,\n    g.[user_id],\n    ds.[fake_date_num],\n    ds.[shared_counter]\nfrom \n    [Dashboards_shared] ds\nleft join \n    [Dashboards] d \n    on d.[dashboard_id] = ds.[dashboard_id]\nleft join \n    [Dim_user_groups_mapped] g on g.[user_group] = ds.[user_group_id]\nwhere object_type = 'group'\n\n"
